mapper.select:
select * from BOAT_ATTENDANCE
 where empno = #{empno}

order by reg_date desc

select * from BOAT_ATTENDANCE 
where REG_DATE =  TO_CHAR(SYSDATE, 'YYYYMMDD')
and EMPNO = #{EMPNO}

mapper.update:
UPDATE BOAT_ATTENDANCE
SET off_time = #{OFF_TIME}
WHERE REG_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD')
and empno = #{EMPNO}

SELECT ON_TIME FROM BOAT_ATTENDANCE 
where reg_date = TO_CHAR(SYSDATE, 'YYYYMMDD')
and empno = #{EMPNO}

  UPDATE BOAT_ATTENDANCE
SET WORK_TIME = #{WORK_TIME}
WHERE REG_DATE = TO_CHAR(SYSDATE, 'YYYYMMDD')
and empno = #{EMPNO}
