mapper.select:
select * from (select b.*, rownum rnum from (select file_board.* , nvl(CNT ,0) CNT from file_board left outer join (select F_COMMENT_NUM,count(*) CNT from FILE_COMMENT group by F_COMMENT_NUM order by CNT desc)
on FILE_NUM = F_COMMENT_NUM
FILE_NAME like #{searchinput}
FILE_SUBJECT like #{searchinput}
and dept =#{dept}
order by FILE_DATE desc
order by FILE_READCOUNT desc
order by CNT desc
)b where rownum<= #{end} )
where rnum>=#{start} and rnum<= #{end}

select count(*) 
	from file_board 





							FILE_NAME like #{searchinput}  


						  FILE_SUBJECT like #{searchinput}




						and dept =#{dept}

mapper.update:
	update file_board
  set FILE_READCOUNT  = FILE_READCOUNT +1
  where FILE_NUM = #{number}

mapper.select:
select * from file_board
  where FILE_NUM=#{num}
  and FILE_PASS=#{pass}

mapper.update:
  	  update file_board  set FILE_RE_SEQ  = FILE_RE_SEQ +1 
					where FILE_RE_REF  = #{FILE_RE_REF }
					and FILE_RE_SEQ   >  #{FILE_RE_SEQ }

mapper.delete:

  delete from file_board  
			where FILE_RE_REF  = #{FILE_RE_REF} 
			and FILE_RE_LEV   > =#{FILE_RE_LEV} 
			and FILE_RE_SEQ   > =#{FILE_RE_SEQ} 
			and FILE_RE_SEQ   < =(  
							 		nvl((select min(FILE_RE_SEQ) -1 
									  from file_board
									  where FILE_RE_REF = #{FILE_RE_REF} 
									  and FILE_RE_LEV = #{FILE_RE_LEV}
									  and FILE_RE_SEQ  > #{FILE_RE_SEQ}),
									(select max(FILE_RE_SEQ)
								 		from file_board
										 where FILE_RE_REF = #{FILE_RE_REF})))
